function parsePinCode(inputString: string) {
    var instructions = inputString.split('\n');

    let numpad = [[1, 2, 3],
                  [4, 5, 6],
                  [7, 8, 9]];
    let currentPosition = [1, 1];
    let code = [];
    for (let i = 0; i < instructions.length; i++) {
        let instruction = instructions[i];
        for (let j = 0; j < instruction.length; j++) {
            let direction = instruction.charAt(j);
            move(currentPosition, direction);
        }
        code.push(numpad[currentPosition[1]][currentPosition[0]]);
    }
    return parseInt(code.join(''));
}

function move(position, direction) {
    switch (direction) {
        case 'U':
            if (position[1] > 0)
                position[1]--;
            break;
        case 'D':
            if (position[1] < 2)
                position[1]++;
            break;
        case 'L':
            if (position[0] > 0)
                position[0]--;
            break;
        case 'R':
            if (position[0] < 2)
                position[0]++;
            break;
    }
}

